const request = require('request')
const semver = require('semver')
const chalk = require('chalk')
const packageConfig = require('../package.json')

module.exports = (done,next,spinner) => {
  // Ensure minimum supported node version is used
  if (!semver.satisfies(process.version, packageConfig.engines.node)) {
    return console.log(chalk.red(
      '  You must upgrade node to >=' + packageConfig.engines.node + '.x to use vue-cli'
    ))
  }

  request({
    url: 'https://registry.npmjs.org/cli-vue',
    timeout: 5000
  }, (err, res, body) => {
    if (!err && res.statusCode === 200&&body!=="undefined") {
      console.log()
      console.log(chalk.yellow('https://registry.npmjs.org/cli-vue/version:'+JSON.parse(body)['dist-tags'].latest))
      const latestVersion = JSON.parse(body)['dist-tags'].latest
      const localVersion = packageConfig.version
      if (semver.lt(localVersion, latestVersion)) {
        console.log(chalk.yellow('  A newer version of vue-cli is available.'))
        console.log()
        console.log('https://registry.npmjs.org/cli-vue/version:' + chalk.green(latestVersion))
        console.log('当前version:' + chalk.red(localVersion))
        console.log()
        done()
      }else{
        next()
      }
    }else{
      spinner.stop();
      console.log();
      console.log(chalk.green('网络不稳定，请重试！'))
    }
    
  })
}
